let lojinha = document.getElementById("lojinha");

let div = document.createElement("div");
itens.forEach((produto, index) => {
    const produtoDiv = document.createElement("div");
    produtoDiv.id = "item";
    produtoDiv.style.display = "flex"
    produtoDiv.style.width = 300;
    produtoDiv.style.height = 300;

    // Atributos devem ser definidos independentemente do índice
    produtoDiv.setAttribute("nome", produto.nome);
    produtoDiv.setAttribute("img", produto.img);
    produtoDiv.setAttribute("preco", produto.preco);
    
    // Mostra apenas os 3 primeiros produtos
    if (index > 2) {
        produtoDiv.setAttribute("class", "hide");
    } else {
        produtoDiv.setAttribute("class", "show"); // Para mostrar os 3 primeiros
    }

    produtoDiv.innerHTML = `
        <h2>${produto.nome}</h2> 
        <img src="${produto.img}" alt="Imagem de ${produto.nome}" />
        <p>Preço: R$${produto.preco}</p>
        <button class='adicionar' style="font-size: 0.9em;height: 50px; width: 200px">Adicionar ao carrinho</button>
    `;
    lojinha.appendChild(produtoDiv);
});

let produtos = [];
let botoes = document.querySelectorAll(".adicionar");

botoes.forEach((botao) => {
    botao.addEventListener("click", () => {
        let item = botao.closest("#item"); // Corrigido para buscar o ancestral correto
        if (!item) {
            console.error("Elemento não encontrado");
            return; // Sai da função se não encontrar o elemento
        }
        const nome = item.getAttribute("nome");
        const img = item.getAttribute("img");
        const preco = item.getAttribute("preco");

        produtos.push({ nome, img, preco });
        adicionar(produtos); // Chama a função para adicionar os produtos
    });
});

function adicionar(produtos) {
    const ul = document.getElementById('add-list'); // ul que armazenará as compras do carrinho

    // Limpa a lista antes de adicionar
    ul.innerHTML = ''; // Limpa os itens existentes na ul

    produtos.forEach((produto) => {
        const li = document.createElement('li'); // Cria elemento li
        li.style.display = "flex";
        li.style.justifyContent = "flex-start";
        li.innerHTML = `<img style="height:100px;width:100px" src="${produto.img}" />
            <div style="display: flex; align-items: center; flex-direction: column">
                <h2 style="font-size: 10px; height: auto; width: auto">${produto.nome}</h2>
                <p>R$ ${produto.preco}</p>
            </div>`;
        ul.style.height = '100px'; // Define a altura da ul
        ul.style.overflowY = 'scroll'; // Define o overflowY da ul como scroll
        ul.appendChild(li); // Adiciona a li para cada vez que o forEach for executado
    });

    alert('Adicionado com sucesso!'); // Mover o alert para fora do loop
}

function clicar() {
    let divs = Array.from(document.getElementsByClassName("hide")); // Converte a coleção em um array

    if (divs.length === 0) {
        console.log("Nenhum elemento encontrado com a classe 'hide'");
        return; // Sai da função se não houver elementos
    }

    // Itera sobre a cópia do array
    divs.forEach(div => {
        div.classList.remove("hide"); // Remove a classe "hide"
        div.classList.add("show");     // Adiciona a classe "show"
    });
}

